package urban.uri_online_judge.beginner._2147;

import org.junit.Test;
import urban.common.SampleBuilder;

import java.io.ByteArrayOutputStream;
import java.io.IOException;
import java.io.InputStream;
import java.io.PrintStream;

import static org.junit.Assert.assertEquals;
import static org.junit.Assert.assertNotNull;

public class MainTest {

    @Test
    public void shouldCreateInstance() {
        assertNotNull(new Main());
    }

    @Test
    public void shouldHandleLowerLimit() throws IOException {
        InputStream input = new SampleBuilder().appendln(1).append("galopeira").buildAsInputStream();
        String output = Main.main(input);
        String expectedOutput = new SampleBuilder().appendln(0.09).build();
        assertEquals(expectedOutput, output);
    }

    @Test
    public void shouldHandleHigherLimit() throws IOException {
        InputStream input = new SampleBuilder().appendln(1).append("").buildAsInputStream();
        String output = Main.main(input);
        String expectedOutput = new SampleBuilder().appendln("100.00").build();
        assertEquals(expectedOutput, output);
    }

    @Test
    public void shouldHandleURISample() throws IOException {
        InputStream input = new SampleBuilder().appendln(3).appendln("galopeira").appendln("galopeeeeeeeeeeeeeeeeeira").appendln("galopeeira").buildAsInputStream();
        String output = Main.main(input);
        String expectedOutput = new SampleBuilder().appendln(0.09).appendln(0.25).appendln("0.10").build();
        assertEquals(expectedOutput, output);
    }

    @Test
    public void shouldRunThroughMain() throws IOException {
        InputStream input = new SampleBuilder().appendln(3).appendln("galopeira").appendln("galopeeeeeeeeeeeeeeeeeira").appendln("galopeeira").buildAsInputStream();
        System.setIn(input);

        ByteArrayOutputStream outContent = new ByteArrayOutputStream();
        System.setOut(new PrintStream(outContent));

        Main.main(new String[]{});

        String expectedOutput = new SampleBuilder().appendln(0.09).appendln(0.25).appendln("0.10").build();

        assertEquals(expectedOutput, outContent.toString());
    }

}